// Language Detection and Input Parsing
const userMessage = $input.item.json.chatInput || '';
const sessionId = $input.item.json.sessionId || 'default';

// Detect language (simple heuristic - check for Cyrillic characters)
const hasCyrillic = /[–ê-–Ø–∞-—è–Å—ë]/.test(userMessage);
const communicationLanguage = hasCyrillic ? 'russian' : 'english';

// Parse key information from message
const messageData = {
  originalMessage: userMessage,
  communicationLanguage: communicationLanguage,
  sessionId: sessionId,
  timestamp: new Date().toISOString(),

  // Extract potential entities
  hasPublication: /sostav|forbes|rbc|vc\.ru|cossa|adindex/i.test(userMessage),
  hasMetrics: /\d+%|\$\d+|‚ÇΩ\d+|–º–∏–ª–ª–∏–æ–Ω|million|–≤—ã—Ä–æ—Å–ª–∏|—Ä–æ—Å—Ç|growth/.test(userMessage),
  hasClientName: true, // Will be refined in OpenAI

  // Determine if this is initial request or follow-up
  isInitialRequest: !userMessage.toLowerCase().includes('–¥–∞') && 
                     !userMessage.toLowerCase().includes('–Ω–µ—Ç') &&
                     !userMessage.toLowerCase().includes('yes') &&
                     !userMessage.toLowerCase().includes('no'),

  // Prepare for next nodes
  needsResearch: userMessage.length < 200, // Short messages may need more context
  readyForGeneration: userMessage.length > 200 && /–∫–ª–∏–µ–Ω—Ç|client|–ø—Ä–æ–µ–∫—Ç|project|—Ä–µ–±—Ä–µ–Ω–¥–∏–Ω–≥|rebrand/.test(userMessage)
};

return { json: messageData };

// Combine research with original message
const originalData = $('Language Detection & Parsing').item.json;
const researchResponse = $input.item.json.choices[0].message.content;

return {
  json: {
    ...originalData,
    researchContext: researchResponse,
    enrichedMessage: originalData.originalMessage + "\n\n[Research Context: " + researchResponse + "]",
    readyForGeneration: true
  }
};

// Extract and parse OpenAI response
const response = $input.item.json.choices[0].message.content;
const originalData = $('Prepare OpenAI Request').item.json;

// Parse the generated case study
const caseStudy = {
  content: response,
  userEmail: originalData.userEmail || 'user@example.com', // Should be collected in chat
  communicationLanguage: originalData.communicationLanguage,
  generatedAt: new Date().toISOString(),
  sessionId: originalData.sessionId
};

// Basic validation
const validations = {
  hasRussianText: /[–ê-–Ø–∞-—è–Å—ë]/.test(response),
  hasQuotes: (response.match(/>/g) || []).length >= 2, // At least 2 blockquotes
  hasTitle: /^[^\n]+/.test(response),
  lengthOk: response.length >= 1500 && response.length <= 4000,
  hasDDVBBranding: /DDVB|–∞–≥–µ–Ω—Ç—Å—Ç–≤–æ|–∫–æ–º–∞–Ω–¥–∞/.test(response)
};

const allValid = Object.values(validations).every(v => v === true);

return {
  json: {
    ...caseStudy,
    validations: validations,
    isValid: allValid,
    validationSummary: allValid ? '‚úÖ All quality checks passed' : '‚ö†Ô∏è Some quality checks failed: ' + JSON.stringify(validations)
  }
};
